contract PaymentSplitter {
    event PayeeAdded(address account, uint256 shares);
    event PaymentReleased(address to, uint256 amount);
    event ERC20PaymentReleased(IERC20 indexed token, address to, uint256 amount);
    event PaymentReceived(address from, uint256 amount);


    uint256 private _totalShares;
    uint256 private _totalReleased;

    mapping(address => uint256) private _shares;
    mapping(address => uint256) private _released;
    address[] private _payees;

    mapping(IERC20 => uint256) private _erc20TotalReleased;
    mapping(IERC20 => mapping(address => uint256)) private _erc20Released;


     receive() external payable virtual {
        emit PaymentReceived(msg.sender, msg.value);
    }

    function totalShares() public view returns (uint256) {
        return _totalShares;
    }

    function totalReleased() public view returns (uint256) {
        return _totalReleased;
    }

    function totalReleased(IERC20 token) public view returns (uint256) {
        return _erc20TotalReleased[token];
    }

    function shares(address account) public view returns (uint256) {
        return _shares[account];
    }

    function released(address account) public view returns (uint256) {
        return _released[account];
    }

    function released(IERC20 token, address account) public view returns (uint256) {
        return _erc20Released[token][account];
    }

    function payee(uint256 index) public view returns (address) {
        return _payees[index];
    }

    function release(address payable account) public virtual {
        require(_shares[account] > 0, "PaymentSplitter: account has no shares");

        uint256 totalReceived = address(this).balance + totalReleased();
        uint256 payment = _pendingPayment(account, totalReceived, released(account));

        require(payment != 0, "PaymentSplitter: account is not due payment");

        _released[account] += payment;
        _totalReleased += payment;

        Address.sendValue(account, payment);
        emit PaymentReleased(account, payment);
    }

    function release(IERC20 token, address account) public virtual {
        require(_shares[account] > 0, "PaymentSplitter: account has no shares");

        uint256 totalReceived = token.balanceOf(address(this)) + totalReleased(token);
        uint256 payment = _pendingPayment(account, totalReceived, released(token, account));

        require(payment != 0, "PaymentSplitter: account is not due payment");

        _erc20Released[token][account] += payment;
        _erc20TotalReleased[token] += payment;

        SafeERC20.safeTransfer(token, account, payment);
        emit ERC20PaymentReleased(token, account, payment);
    }

    function _pendingPayment(
        address account,
        uint256 totalReceived,
        uint256 alreadyReleased
    ) private view returns (uint256) {
        return (totalReceived * _shares[account]) / _totalShares - alreadyReleased;
    }

    function _addPayee(address account, uint256 shares_) internal {
        require(account != address(0), "PaymentSplitter: account is the zero address");
        require(shares_ > 0, "PaymentSplitter: shares are 0");
        require(_shares[account] == 0, "PaymentSplitter: account already has shares");

        _payees.push(account);
        _shares[account] = shares_;
        _totalShares = _totalShares + shares_;
        emit PayeeAdded(account, shares_);
    }
}

pragma solidity >=0.8.4;

contract LaunchMyNFT is ERC721, PaymentSplitter {
    uint256 public totalSupply;
    uint256 public cost;
    uint256 public whitelistCost;
    uint256 public maxSupply;
    address public owner;
    string private baseURI;
    bytes32 private merkleRoot;
    bool public whitelistActive;
    bool initialized;

    mapping(address => bool) public whitelistClaimed;

    error WhitelistActive(string msg);
    error WhitelistInactive(string msg);
    error SoldOut(string msg);
	error InsufficientFunds(string msg);
	error AlreadyClaimed(string msg);
	error InvalidProof(string msg);
	error NotOwner(string msg);
    error AlreadyInitialized(string msg);

    event Minted(address indexed owner, string tokenURI, uint256 indexed mintTime);

    constructor() ERC721("", "") {
        owner = tx.origin;
    }

    function init(bytes calldata data) external {
        if (initialized) revert AlreadyInitialized("Already initialized");
        initialized = true;
        (
            address[] memory payees_,
            uint256[] memory shares_,
            string memory _name, 
            string memory _symbol, 
            string memory _baseURI,
            bool _whitelistActive, 
            address _owner,
            uint256 _cost, 
            uint256 _whitelistCost,
            uint256 _maxSupply
        ) = abi.decode(data, (address[], uint256[], string, string, string, bool, address, uint256, uint256, uint256));
            _paymentSplitter(payees_, shares_);
            owner = _owner;
            name = _name;
            symbol = _symbol;
            cost = _cost;
            maxSupply = _maxSupply;
            baseURI = _baseURI;
            whitelistActive = _whitelistActive;

            if (_whitelistActive) {
                whitelistCost = _whitelistCost;
            }
    }

    function setWhitelist(bytes32 _merkleRoot) external {
		if (msg.sender != owner) revert NotOwner("Not owner");
        merkleRoot = _merkleRoot;
    }

    function toggleWhitelist() external {
        if (msg.sender != owner) revert NotOwner("Not owner");
        whitelistActive = !whitelistActive;
    }

    function mint() external payable {
        if (whitelistActive) revert WhitelistActive("Whitelist active");
        if (totalSupply + 1 > maxSupply) revert SoldOut("Sold out");
		if (msg.value != cost) revert InsufficientFunds("Insufficient funds");
        totalSupply++;
        _safeMint(msg.sender, totalSupply);
    }

    function whitelistMint(bytes32[] calldata _merkleProof) external payable {
        if (!whitelistActive) revert WhitelistInactive("Whitelist is inactive");
		if (whitelistClaimed[msg.sender]) revert AlreadyClaimed("Already claimed");
   		if (totalSupply + 1 > maxSupply) revert SoldOut("Sold out");
		if (msg.value != whitelistCost) revert InsufficientFunds("Insufficient funds");

        bytes32 leaf = keccak256(abi.encodePacked(msg.sender));

		if (!MerkleProof.verify(_merkleProof, merkleRoot, leaf)) revert InvalidProof("Invalid proof");
		//require(MerkleProof.verify(_merkleProof, merkleRoot, leaf), "Invalid proof");

        whitelistClaimed[msg.sender] = true;
        totalSupply++;
        _safeMint(msg.sender, totalSupply);
        emit Minted(msg.sender, tokenURI(totalSupply), block.timestamp);
    }

    function tokenURI(uint256 tokenId) public view override(ERC721) returns (string memory) {
        return
            string(
                abi.encodePacked(
                    baseURI,
                    Strings.toString(tokenId),
                    ".json"
                )
            );
    }

    function _paymentSplitter(address[] memory _payees, uint256[] memory shares_) internal {
        require(_payees.length == shares_.length, "PaymentSplitter: payees and shares length mismatch");
        require(_payees.length > 0, "PaymentSplitter: no payees");

        for (uint256 i = 0; i < _payees.length; i++) {
            _addPayee(_payees[i], shares_[i]);
        }
    }
}

